DataSource.RecordSource:
IF OBJECT_ID('tempdb.dbo.#fishdummy1', 'U') IS NOT NULL DROP TABLE #fishdummy1;
CREATE TABLE #fishdummy1 (CDFName VARCHAR(max), CDFDispalayValue VARCHAR(max),CDFType VARCHAR(max), CDFLabel VARCHAR(max))
DECLARE @xml_var XML SET @xml_var = ( select CustomControlData from cases where caseid=-9999 and CustomControlData is not null
)
DECLARE @CDFName VARCHAR(1000), @CDFDispalayValue VARCHAR(1000), @CDFType VARCHAR(1000), @CDFLabel VARCHAR(1000)
INSERT INTO #fishdummy1(CDFName,CDFDispalayValue,CDFType,CDFLabel) SELECT * FROM ( SELECT Tbl.Col.value('@CDFName', 'varchar(1000)') as CDFName, Tbl.Col.value('@CDFDisplayValue', 'varchar(1000)') as CDFDispalayValue, Tbl.Col.value('@CDFType', 'varchar(1000)') as CDFType, Tbl.Col.value('@CDFLabel', 'varchar(1000)') as CDFLabel FROM @xml_var.nodes('//Data') Tbl(Col) )a
DECLARE @virus NVARCHAR(20)= 'False' IF EXISTS ( Select 1 From Caseprocedures CP --INNER JOIN Procedures P ON CP.procedureid = P.procedureid AND CP.testid = P.testid WHERE CP.caseid = -9999 AND (CP.status = 'Fulfilled' or CP.status='In Progress') and ISNULL(CP.OtherProcedureName,CP.ProcedureName) IN ('Cryptosporidium','Giardia lamblia','Entamoeba histolytica') ) SET @virus = 'True'
SELECT CDF.TestName AS CDFName,Case when CDF.Result='NOTDETECTED' THEN 'NOT DETECTED' ELSE CDF.Result END Results, ISNULL(CP.OtherProcedureName,CP.ProcedureName) AS TestName FROM CaseProcedures CP --INNER JOIN Procedures P ON CP.procedureid = P.procedureid AND CP.testid = P.testid LEFT JOIN (Select REPLACE(cdfname,'result','') TestName,CDFDispalayValue as Result,LTRIM(RTRIM(REPLACE(CDFLabel,'result',''))) AS CDFLabel
from #fishdummy1 ) CDF ON ISNULL(CP.OtherProcedureName,CP.ProcedureName)=CDFLabel WHERE CP.caseid = -9999 AND (CP.status = 'Fulfilled' or CP.status='In Progress') and ISNULL(CP.OtherProcedureName,CP.ProcedureName) IN ('Cryptosporidium','Giardia lamblia','Entamoeba histolytica')
